"use client";

import { useLanguage } from "@/context/LanguageContext";
import { Kaushan_Script } from "next/font/google";
import Image from "next/image";
import React from "react";
import { FaArrowRight } from "react-icons/fa";


const kaushan = Kaushan_Script({
  subsets: ["latin"],
  weight: ["400"],
});
const Hero = () => {
  // const { t } = useLanguage();

  return (
    <div className="relative w-full h-[90vh] md:h-[120vh]  bg-[url('/images/home/hero_bg.webp')] bg-cover bg-no-repeat bg-center z-10">
      <div className="absolute top-0 left-0 w-full h-full bg-gray-800 opacity-25"></div>
      <h1 className="text-5xl">{t.greeting}</h1>
      <div className="absolute top-44 grid grid-cols-3 gap-2 justify-between items-start w-[80%] left-[10%] mx-auto ">
        <div>
          <div className="border-t-2 border-b-2 py-10 border-t-border border-b-border w-full h-32 flex flex-col items-center justify-center">
            <p className="text-white text-sm md:text-md font-bold tracking-[0.17rem]">
              <span
                className={`${kaushan.className} text-lg md:text-xl font-extrabold underline underline-offset-8 italic pe-1`}
              >
                Let's
              </span>
              CREATE SOMETHING GREAT
            </p>
          </div>
          <br />
          <p className="inline text-xs text-white font-light cursor-pointer">Get In Touch &nbsp; <FaArrowRight className="inline" size={14} />  </p>
        </div>
        <div className="mx-auto">
          <Image
            src="/images/home/hero.webp"
            alt="hero"
            width={100}
            height={100}
            className="w-72 object-contain -mt-8 md:-mt-16"
          />
        </div>
        <div className="border-t-2 border-b-2 py-10 border-t-white border-b-white w-full  h-32 flex flex-col items-center justify-center text-center">
          <p className="text-white text-2xl md:text-4xl font-normal font-graphik tracking-[0.17rem] ">
            WE MAKE IT
          </p>
          <p className="text-[#FFCBE0] text-2xl  md:text-4xl font-bold tracking-[14px] text-center">HAPPEN</p>
        </div>
      </div>
    </div>
  );
};

export default Hero;
