"use client";

type Props = {
    children: any
}
const WavyCard1 = ({ children }:Props) => {
  return (
    <div className="relative w-96 h-64">
     
      <svg
        className="absolute inset-0 w-full h-full"
        viewBox="0 0 100 100"
        preserveAspectRatio="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          /* The `d` (path data) attribute is what defines the shape.
            - `M0,0 L100,0 L100,85`: Draws a line from the top-left to the top-right, and then down to the right side of the wavy part.
            - `C...`: The "C" commands are cubic Bezier curves that create the smooth, wavy effect at the bottom.
            - `L0,0 Z`: Closes the path.

            You can get this path data from a vector graphics program like Figma or Adobe Illustrator.
          */
          d="M0,0 L100,0 L100,85 C95,95 85,95 80,85 C75,95 65,95 60,85 C55,95 45,95 40,85 C35,95 25,95 20,85 C15,95 5,95 0,85 L0,0 Z"
          /* `fill` and `stroke` attributes can be set with CSS or Tailwind classes.
            We use a Tailwind class `bg-white` and `border-2 border-blue-500` on the container,
            but for the SVG you'll want to use the attributes directly for better control.
          */
          fill="#ffffff" // White background color
          stroke="#3b82f6" // Blue border color
          strokeWidth="1.5"
        />
      </svg>

      {/* This is the container for your content.
        - `relative z-10`: Ensures the content appears above the SVG background.
        - `p-4`: Adds padding around the content.
      */}
      <div className="relative z-10 p-4">
        {children}
      </div>
    </div>
  );
};

export default WavyCard1;
